mapper.insert:
INSERT INTO t_board_img
( iboard
, img)
VALUES
( #{iboard}
, #{img}
)

mapper.update:
UPDATE t_board
SET    hitCount = hitCount + 1
WHERE    iboard = #{iboard}

UPDATE t_board
SET    title = #{title}
,      ctnt  = #{ctnt}
WHERE iboard = #{iboard}

UPDATE t_board
SET    delYN  = 'Y'
WHERE  iboard = #{iboard};

mapper.select:
SELECT COUNT(*) FROM t_board WHERE 1 = 1
AND provider = #{provider}
AND delYN = 'N'
