mapper.insert:
 INSERT INTO t_user
(
  email
, pw
, nm
, nick
, tel
, authCd
, provider
)
VALUES
(
   #{email}
 , #{pw}
 , #{nm}
 , #{userNick}
 , #{tel}
 , #{authCd}
 , #{provider}
 )

mapper.update:
UPDATE t_user
SET authCd = null
WHERE email = #{email} AND authCd = #{authCd}

UPDATE t_user


        profileImg = #{param.profileImg}


    , nick = #{userNick}


WHERE iuser    = #{param.iuser}

mapper.select:
SELECT email
      ,pw
      ,nm
      ,nick AS userNick
      ,tel
FROM  t_user
WHERE 1 = 1

    AND email = #{email}


    AND nick = #{userNick}


    AND tel  = #{tel}

SELECT email
FROM   t_user
WHERE 1 = 1
AND nm  = #{nm}
AND tel = #{tel}

mapper.update:
UPDATE t_user
SET   pw    = #{pw}
WHERE email = #{email}

UPDATE t_user
SET   pw    = #{pw}
WHERE iuser = #{iuser}
