mapper.insert:
 INSERT INTO t_user
(
  email
, pw
, nm
, nick
, tel
, authCd
, provider
)
VALUES
(
   #{email}
 , #{pw}
 , #{nm}
 , #{userNick}
 , #{tel}
 , #{authCd}
 , #{provider}
 )

mapper.update:
UPDATE t_user
SET authCd = null
WHERE email = #{email} AND authCd = #{authCd}

mapper.insert:
INSERT INTO t_user_profile
(
  iuser
, profileImg
)
VALUES
(
  #{iuser}
, #{profileImg}
)

mapper.update:
UPDATE t_user
SET nick    = #{userNick}
WHERE iuser = #{iuser}
